const queryString = window.location.search
const urlParams = new URLSearchParams(queryString)
const id = urlParams.get("id")
//let panier = ""   Pas nécessaire voir commentaire ligne 86
console.log({id});

if (id != null){
    let itemPrice = 0
    let imgUrl, altText
    let productName
}

fetch(`http://localhost:3000/api/products/${id}`)
   .then((reponse) => reponse.json())
   .then((res) => getDataKanap(res)) 

function getDataKanap(product) {   // product au lieu de panier est plus adapté, toujours nommer ces variables de manière explicite à quoi ça correspond
    //récupération de toutes les données dans l'objet kanap
    const {altTxt, colors, description, imageUrl, name, price, id}  = product  // product à la place de panier
    //les données sont passées dans des fonctions qui vont les afficher
    itemPrice = price
    imgUrl = imageUrl
    altText = altTxt
    productName = name 
    makeImage(imageUrl, altTxt)
    makeTitle(name)
    makePrice(price)
    makeDescription(description)
    makeColors(colors)
  //  console.log(panier)
}
//localStorage.clear()
function makeImage(imageUrl, altTxt){
    const image = document.createElement("img")
    image.src = imageUrl
    image.alt = altTxt
    const parent = document.querySelector(".item__img")
    if(parent != null)parent.appendChild(image)
    return image
} 
function makeTitle(name) {
    const h1 = document.querySelector("#title")
    if(h1 != null)h1.textContent = name
    return name
}
function makePrice(price) {
    const span = document.querySelector("#price")
   if (span !=null)span.textContent = price
}
function makeDescription(description) {
    const p = document.querySelector("#description")
    if (p != null)p.textContent = description
}
function makeColors(colors) {
    const select = document.querySelector("#colors")
    colors.forEach((color) => {
        const option = document.createElement("option")
        option.value = color
        option.textContent = color
        select.appendChild(option)
    })
}
const buttonToCart = document.querySelector("#addToCart")
buttonToCart.addEventListener("click", buttonEvent)
//console.log(buttonEvent) j'ai du mal à voir ce que ce console.log peut d'apporter
function buttonEvent(){
    const color = document.querySelector("#colors").value
    const quantity = document.querySelector("#quantity").value
  if (invalidCommand(color, quantity)) return
    saveCommand(color,quantity)
    redirectToCart()
}
function invalidCommand(color, quantity){
    if (color == null || color === "" ) {
        alert("Vous devez sélectionner une couleur. Merci")
        return true // il faut retourner une valeur parce ligne 69, tu as une condition sur le retour de la fonction, si tu retourne rien, peu importe que le formulaire soit bon ou pas, le comportement restera inchangé
        }
